import { useEffect,useRef } from "react";

export function UseKey(Key,action){
    useEffect(
        function () {
            function callback(e){
                if (e.code.toLowerCase() === Key.toLowerCase()) {
                    action()
                    console.log('closing');
                  }

            }
          document.addEventListener('keydown', function (e) {

          });
          return function(){
            document.removeEventListener('keydown',callback)
          }
        },
        [action,Key],
      );
}
