import {useState, useEffect} from 'react';

const tempMovieData = [
  {
    imdbID: 'tt1375666',
    Title: 'Inception',
    Year: '2010',
    Poster:
      'https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg',
  },
  {
    imdbID: 'tt0133093',
    Title: 'The Matrix',
    Year: '1999',
    Poster:
      'https://m.media-amazon.com/images/M/MV5BNzQzOTk3OTAtNDQ0Zi00ZTVkLWI0MTEtMDllZjNkYzNjNTc4L2ltYWdlXkEyXkFqcGdeQXVyNjU0OTQ0OTY@._V1_SX300.jpg',
  },
  {
    imdbID: 'tt6751668',
    Title: 'Parasite',
    Year: '2019',
    Poster:
      'https://m.media-amazon.com/images/M/MV5BYWZjMjk3ZTItODQ2ZC00NTY5LWE0ZDYtZTI3MjcwN2Q5NTVkXkEyXkFqcGdeQXVyODk4OTc3MTY@._V1_SX300.jpg',
  },
];

const tempWatchedData = [
  {
    imdbID: 'tt1375666',
    Title: 'Inception',
    Year: '2010',
    Poster:
      'https://m.media-amazon.com/images/M/MV5BMjAxMzY3NjcxNF5BMl5BanBnXkFtZTcwNTI5OTM0Mw@@._V1_SX300.jpg',
    runtime: 148,
    imdbRating: 8.8,
    userRating: 10,
  },
  {
    imdbID: 'tt0088763',
    Title: 'Back to the Future',
    Year: '1985',
    Poster:
      'https://m.media-amazon.com/images/M/MV5BZmU0M2Y1OGUtZjIxNi00ZjBkLTg1MjgtOWIyNThiZWIwYjRiXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg',
    runtime: 116,
    imdbRating: 8.5,
    userRating: 9,
  },
];

const KEY = 'aabfc447';

const average = arr =>
  arr.reduce((acc, cur, i, arr) => acc + cur / arr.length, 0);
export default function App() {
  //to fatch data asap when App mount
  //dont setstate in the top level of render logic!!
  const [movies, setMovies] = useState([]);
  const [watched, setWatched] = useState(tempWatchedData);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const query = 'interstellar';
  // it will keep fetching data from the api because setting state in render logic will immediately cause component to re-render itself and then run the function again, fetch again then setMovie again --- infinity loop!!

  //useEffect

  //the function will only be executed on mount(first render) when using [] as second paremeter in useEffect
  //side effect is basically any interation between a React component and the world outside the component. also think of as a side as code that acually does something, for example Data fetching

  //side effect should not be render logic, we can build side effect in :
  //1. eventHandler: onClick, onSubmit
  //2. useEffect: need to write some code need to be executed when component initial render , and Effect allow us to write code that will run at different moment: mount,re-render, or unmount. used to keep a component synchronized with some external system

  // useEffect(function () {
  //   fetch(``)
  //     .then(res => res.json())
  //     .then(data => {
  //       console.log(data);
  //       setMovies(data.Search);
  //     });
  // }, []);

  useEffect(function () {
    async function fetchMovies() {
      try {
        setIsLoading(true);
        const res = await fetch(
          `https://www.omdbapi.com/?i=tt3896198&apikey=${KEY}&s=${query}`,
        );
        const data = await res.json();
        //in React strict mode, effect will run twice, therefore when console.log(data), it will console twice
        //but in production ,it wont happened if remove the strictmode

        //catch error
        if (!res.ok)
          throw new Error('Something went wrong with fetching movies');
        //after the error is being thrown. react will not run the below code
        console.log(data);
        setMovies(data.Search);

        console.log(movies); //[],still state;
      } catch (err) {
        console.log(err.message);
        setError(err.message);
      } finally {
        //alway run at the end
        setIsLoading(false);
      }
    }
    fetchMovies();
  }, []);

  return (
    <>
      <NavBar>
        <Logo />
        <Search />
        <NumResult movies={movies} />
      </NavBar>

      <Main>
        {/* building compostive component by passing element as props  */}
        {/* <Box element={<MovieList movies={movies} />} />
        <Box
          element={
            <>
              <WatchSummary watched={watched} />
              <WatchedMovieList watched={watched} />
            </>
          }
        /> */}

        {/* building compostive compoent by passing element as children */}
        <Box>
          {/* {isLoading ? <Loader /> : <MovieList movies={movies} />} */}
          {/* three exclusive condition */}
          {isLoading && <Loader />}
          {!isLoading && !error && movies.length > 0 && (
            <MovieList movies={movies} />
          )}
          {error && <ErrorMessage message={error} />}
        </Box>
        <Box>
          <WatchSummary watched={watched} />
          <WatchedMovieList watched={watched} />
        </Box>
      </Main>
    </>
  );
}

