import { useEffect } from "react";

export function UseKey(Key,action){
    useEffect(
        function () {
          document.addEventListener('keydown', function (e) {
            if (e.code.toLowerCase() === Key.toLowerCase()) {
              action()
              console.log('closing');
            }
          });
        },
        [action,Key],
      );
}
